      <div class="description description--2">
        <div class="img-container">
          <div>
            <img src="{{ selectedProject.img2 }}" alt="" />
          </div>
        </div>

        <p>{{ selectedProject.description2 }}</p>
      </div>
      <div class="description description--3">
